import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import the useNavigate hook from react-router-dom
import '../styles/make_task.css';


const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = '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'; // Replace with your actual API key

function MakeTask() {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);


  const handleSubmission = async () => {
    setIsLoading(true); 
    try {
      const systemMessage = {
        role: 'system',
        content: `Transform the text: "${inputText}"`,
      };
      const response = await axios.post(
        API_URL,
        {
          model: 'gpt-3.5-turbo',
          messages: [systemMessage],
        },
        {
          headers: {
            Authorization: `Bearer ${API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );
      const submittedTexts = JSON.parse(localStorage.getItem('submittedTexts')) || [];
      submittedTexts.push(inputText);
      localStorage.setItem('submittedTexts', JSON.stringify(submittedTexts));
      setOutputText(response.data.choices[0].message.content.trim());
    } catch (error) {
      console.error('Error response from OpenAI:', error.response?.data);
      setError('Error transforming text');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSave = () => {
    const savedTexts = JSON.parse(localStorage.getItem('savedTexts')) || [];
    savedTexts.push(outputText);
    localStorage.setItem('savedTexts', JSON.stringify(savedTexts));
    navigate('/'); // Navigate back to Catalog Page after saving
  };

  return (
    <div className="task-container">
      <div className="left-box">
        <textarea
          className="input-area"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          disabled={isLoading}
        />
        <button 
          onClick={handleSubmission} 
          disabled={isLoading || !inputText.trim()} // Disable button if isLoading is true or inputText is empty
        >
          Submit
        </button>
      </div>
      <div className="right-box">
        {error ? (
          <p>{error}</p>
        ) : (
          <>
            <textarea
              className="output-area"
              value={outputText}
              onChange={(e) => setOutputText(e.target.value)}
              disabled={isLoading}
            />
            <button 
              className="save-btn" 
              disabled={isLoading} 
              onClick={handleSave}
            >
              Save
            </button>
          </>
        )}
      </div>
    </div>
  );



}

export default MakeTask;
