import React, { useEffect, useState } from 'react';
import '../styles/catalog_page.css';// You can create a separate CSS file for styling
import { Link } from 'react-router-dom';

function CatalogPage() {
    const [savedTexts, setSavedTexts] = useState([]);

    useEffect(() => {
      const texts = JSON.parse(localStorage.getItem('savedTexts')) || [];
      setSavedTexts(texts);
    }, []);

    return (
      <div className="catalog-container">
        <div className="content-container"> {/* New container for the box and the button */}
          <div className="wrapper-box">
            {savedTexts.map((text, index) => (
              <div key={index} className="text-box">
                {text}
              </div>
            ))}
          </div>
          <Link to="/make-task">
            <button className="new-task-btn">New Task</button>
          </Link>
        </div>
      </div>
    );
  }



export default CatalogPage;
