import React, { useEffect, useState } from 'react';
import { Auth } from 'aws-amplify';
import '../styles/catalog_page.css';
import { useNavigate } from 'react-router-dom';

function CatalogPage() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [savedTexts, setSavedTexts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    checkAuthState();
    const texts = JSON.parse(localStorage.getItem('savedTexts')) || [];
    setSavedTexts(texts);
  }, []);

  const checkAuthState = async () => {
    try {
      await Auth.currentAuthenticatedUser();
      setIsAuthenticated(true);
    } catch {
      setIsAuthenticated(false);
    }
  };

  const handleLogout = async () => {
    try {
      await Auth.signOut();
      setIsAuthenticated(false);
    } catch (error) {
      console.error('Error signing out: ', error);
    }
  };

  const handleTextBoxClick = (index) => {
    navigate(`/text-detail/${index}`);
  };

  const handleNewTaskClick = () => {
    navigate('/make-task');
  };

  if (!isAuthenticated) return null;

  return (
    <div className="catalog-container">
      <div className="header">
        <button onClick={handleLogout}>Log Out</button>
      </div>
      <div className="content-container">
        <div className="wrapper-box">
          {savedTexts.map((text, index) => (
            <div className="text-box" key={index} onClick={() => handleTextBoxClick(index)}>
              <strong>{text.title}</strong>
              <p>{text.content ? text.content.substring(0, 200) : 'No content available'}...</p>
            </div>
          ))}
        </div>
        <button className="new-task-btn" onClick={handleNewTaskClick}>New Task</button>
      </div>
    </div>
  );
}

export default CatalogPage;
