import React, { useState } from 'react';
import axios from 'axios';
import '../styles/make_task.css';// You can create a separate CSS file for styling

const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = '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'; // Replace with your actual API key

function MakeTask() {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');
  const [error, setError] = useState(null);

  const [isLoading, setIsLoading] = useState(false);


  const handleSubmission = async () => {
    setIsLoading(true); 
    try {
      const systemMessage = {
        role: 'system',
        content: `Transform the text: "${inputText}"`, // Update the prompt as per your need
      };
      const response = await axios.post(
        API_URL,
        {
          model: 'gpt-3.5-turbo',
          messages: [systemMessage],
        },
        {
          headers: {
            Authorization: `Bearer ${API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );
      const submittedTexts = JSON.parse(localStorage.getItem('submittedTexts')) || [];
      submittedTexts.push(inputText);
      localStorage.setItem('submittedTexts', JSON.stringify(submittedTexts));
    } catch (error) {
      console.error('Error response from OpenAI:', error.response?.data);
      setError('Error transforming text');
    } finally {
      setIsLoading(false); // Enable the components back, regardless of whether there was an error or not
    }
  };

  const handleSave = () => {
    const savedTexts = JSON.parse(localStorage.getItem('savedTexts')) || [];
    savedTexts.push(outputText);
    localStorage.setItem('savedTexts', JSON.stringify(savedTexts));
  };

  return (
    <div className="task-container">
      <div className="left-box">
        <textarea
          className="input-area"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          disabled={isLoading} // Disable textarea while loading
        />
        <button onClick={handleSubmission} disabled={isLoading}>Submit</button>
      </div>
      <div className="right-box">
        {error ? (
          <p>{error}</p>
        ) : (
          <>
            <textarea
              className="output-area"
              value={outputText}
              onChange={(e) => setOutputText(e.target.value)}
              disabled={isLoading} // Disable textarea while loading
            />
            <button className="save-btn" disabled={isLoading}>Save</button> {/* Disable button while loading */}
          </>
        )}
      </div>
    </div>
  );



}

export default MakeTask;
