import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { API, Auth } from 'aws-amplify';
import '../styles/make_task.css';

const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = process.env.REACT_APP_API_KEY;

function MakeTask() {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [title, setTitle] = useState('');
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const fetchUserId = async () => {
      try {
        const user = await Auth.currentAuthenticatedUser();
        setUserId(user.attributes.sub);
      } catch (error) {
        console.error('Error getting user ID:', error);
      }
    };
    fetchUserId();
  }, []);

  const handleSubmission = async () => {
    setIsLoading(true);
    try {
      const systemMessage = {
        role: 'system',
        content: `Given the information: "${inputText}", please convert it into a structured and comprehensive task list with sequential steps as follows:`
      };
      const response = await axios.post(
        API_URL,
        {
          model: 'gpt-3.5-turbo',
          messages: [systemMessage],
        },
        {
          headers: {
            Authorization: `Bearer ${API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );
      setOutputText(response.data.choices[0].message.content.trim());
    } catch (error) {
      console.error('Error response from OpenAI:', error.response?.data);
      setError('Error transforming text');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSave = async () => {
    const enteredTitle = window.prompt('Please enter a title:');
    if (enteredTitle !== null && userId) {
      try {
        await API.post('apiName', '/endpoint', {
          body: { userId, title: enteredTitle, content: outputText },
        });
        setInputText('');
        setOutputText('');
        navigate('/');
      } catch (error) {
        console.error('Error saving task:', error);
      }
    }
  };

  const handleConfirmSave = async () => {
    if (userId) {
      try {
        await API.post('apiName', '/endpoint', {
          body: { userId, title, content: outputText },
        });
        setShowModal(false);
        setTitle('');
        setInputText('');
        setOutputText('');
        navigate('/');
      } catch (error) {
        console.error('Error confirming save:', error);
      }
    }
  };

  return (
    <div className="task-container">
      <div className="left-box">
        <textarea
          className="input-area"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          disabled={isLoading}
        />
        <button onClick={handleSubmission} disabled={isLoading || !inputText.trim()}>
          Submit
        </button>
      </div>
      <div className="right-box">
        {error ? (
          <p>{error}</p>
        ) : (
          <>
            <textarea
              className="output-area"
              value={outputText}
              onChange={(e) => setOutputText(e.target.value)}
              disabled={isLoading}
            />
            <button className="save-btn" onClick={handleSave} disabled={isLoading || !outputText.trim()}>
              Save
            </button>
          </>
        )}
      </div>
      {showModal && (
        <div className="modal">
          <label htmlFor="title">Title: </label>
          <input type="text" id="title" value={title} onChange={(e) => setTitle(e.target.value)} />
          <button onClick={handleConfirmSave}>Confirm</button>
          <button onClick={() => { setShowModal(false); setTitle(''); }}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default MakeTask;
