import React, { useState } from 'react';
import axios from 'axios';
import '../styles/make_task.css';// You can create a separate CSS file for styling

const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = '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'; // Replace with your actual API key

function MakeTask() {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');
  const [error, setError] = useState(null);

  const handleSubmission = async () => {
    try {
      const systemMessage = {
        role: 'system',
        content: `Transform the text: "${inputText}"`, // Update the prompt as per your need
      };
      const response = await axios.post(
        API_URL,
        {
          model: 'gpt-3.5-turbo',
          messages: [systemMessage],
        },
        {
          headers: {
            Authorization: `Bearer ${API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );
      setOutputText(response.data.choices[0].message.content.trim());
    } catch (error) {
      console.error('Error response from OpenAI:', error.response?.data);
      setError('Error transforming text');
    }
  };

  return (
    <div className="task-container">
      <div className="left-box">
        <textarea
          className="input-area" // Added class for styling
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button className="submit-btn">Submit</button> {/* Moved Submit Button to the left box */}
      </div>
      <div className="right-box">
        {error ? (
          <p>{error}</p>
        ) : (
          <>
            <textarea
              className="output-area" // Added class for styling
              value={outputText}
              onChange={(e) => setOutputText(e.target.value)} // Keep it editable
            />
            <button onClick={handleSubmission}>Save</button> {/* Changed the label to "Save" and moved it to the right box */}
          </>
        )}
      </div>
    </div>
  );

}

export default MakeTask;
