import React from 'react';
import { useParams } from 'react-router-dom';
import '../styles/text_detail_page.css';

function TextDetailPage() {
  const { index } = useParams();
  const texts = JSON.parse(localStorage.getItem('savedTexts')) || [];
  const text = texts[index];

  return (
    <div className="text-detail-container">
      <div className="content-container"> {/* Additional Container */}
        <div className="wrapper-box">
          <div className="text-box">
            <p>{text}</p>
          </div>
        </div>
        <button className="button edit-btn">Edit</button>
        <button className="button delete-btn">Delete</button>
      </div>
    </div>
  );
}

export default TextDetailPage;
